import { type FC } from 'react'
import {
  Button,
  ChainIcon,
  Flex,
  Pill,
  Skeleton,
  Text,
  Box,
  Anchor
} from '../../../primitives/index.js'
import { LoadingSpinner } from '../../LoadingSpinner.js'
import { truncateAddress } from '../../../../utils/truncate.js'
import { type Token } from '../../../../types/index.js'
import { getDeadAddress, type RelayChain } from '@relayprotocol/relay-sdk'
import { CopyToClipBoard } from '../../CopyToClipBoard.js'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faQrcode } from '@fortawesome/free-solid-svg-icons'
import { QRCodeCanvas } from 'qrcode.react'
import { generateQrWalletDeeplink } from '../../../../utils/qrcode.js'
import {
  Popover,
  PopoverContent,
  PopoverPortal,
  PopoverTrigger
} from '@radix-ui/react-popover'

type WaitingForDepositStepProps = {
  fromToken?: Token
  fromChain?: RelayChain
  fromAmountFormatted?: string
  isFetchingQuote?: boolean
  depositAddress?: string
}

export const WaitingForDepositStep: FC<WaitingForDepositStepProps> = ({
  fromToken,
  fromChain,
  fromAmountFormatted,
  isFetchingQuote,
  depositAddress
}) => {
  const qrcodeUrl = generateQrWalletDeeplink(
    fromChain?.vmType,
    fromAmountFormatted,
    fromToken &&
      fromChain &&
      fromToken.address !== getDeadAddress(fromChain?.vmType)
      ? fromToken.address
      : undefined,
    depositAddress,
    fromChain?.id
  )

  return (
    <>
      <Flex
        direction="column"
        css={{
          '--borderColor': 'colors.subtle-border-color',
          border: '1px solid var(--borderColor)',
          borderRadius: 8,
          px: '2',
          py: '3',
          mb: '3',
          gap: '2'
        }}
      >
        <Text style="body2">
          Transfer funds manually from your {fromChain?.displayName} wallet to
          Relayâ€™s deposit address to complete the bridge.
        </Text>
        <Anchor
          href="https://support.relay.link/en/articles/10269920-how-do-deposit-addresses-work"
          target="_blank"
        >
          Learn More
        </Anchor>
      </Flex>
      <Flex css={{ gap: '1' }}>
        <Flex css={{ gap: '1', width: '100%' }} direction="column">
          <Text style="subtitle2">Network</Text>
          <Pill
            color="gray"
            css={{
              display: 'flex',
              alignItems: 'center',
              py: '2'
            }}
            radius="squared"
          >
            <ChainIcon chainId={fromChain?.id} height={20} width={20} />
            <Text style="h6">{fromChain?.displayName}</Text>
          </Pill>
        </Flex>
        <Flex css={{ gap: '1', width: '100%' }} direction="column">
          <Text style="subtitle2">Amount to transfer</Text>
          <Pill
            color="gray"
            css={{
              display: 'flex',
              alignItems: 'center',
              py: '2'
            }}
            radius="squared"
          >
            {fromAmountFormatted ? (
              <>
                {' '}
                <Text style="h6" css={{ fontWeight: '500', mr: '1' }}>
                  {fromAmountFormatted}
                </Text>
                <img
                  alt={fromToken?.name}
                  src={fromToken?.logoURI}
                  width={20}
                  height={20}
                  style={{
                    borderRadius: 9999
                  }}
                />
                <Text style="h6">{fromToken?.symbol}</Text>
              </>
            ) : (
              <Skeleton css={{ height: 24, width: '100%' }} />
            )}{' '}
          </Pill>
        </Flex>
      </Flex>
      <Text style="subtitle2">Relay's Deposit Address</Text>
      <Pill
        radius="rounded"
        color="gray"
        css={{ display: 'flex', alignItems: 'center', gap: '3', p: '3' }}
      >
        {isFetchingQuote ? (
          <Skeleton css={{ height: 21, width: '100%' }} />
        ) : (
          <>
            <Text style="subtitle2" css={{ marginRight: 'auto' }}>
              {truncateAddress(depositAddress, '...', 28, 4)}
            </Text>
            {qrcodeUrl ? (
              <Popover>
                <PopoverTrigger style={{ cursor: 'pointer' }}>
                  <Box css={{ color: 'gray9' }}>
                    <FontAwesomeIcon
                      icon={faQrcode}
                      style={{ width: 16, height: 16 }}
                    />
                  </Box>
                </PopoverTrigger>
                <PopoverPortal>
                  <PopoverContent style={{ zIndex: 9999 }} sideOffset={4}>
                    <Box
                      css={{
                        boxShadow: '0px 1px 5px rgba(0,0,0,0.2)',
                        borderRadius: 8,
                        overflow: 'hidden',
                        transition: 'transform 0.3s ease',
                        '&:hover': {
                          transform: 'scale(1.25)'
                        }
                      }}
                    >
                      <Box
                        css={{
                          background: 'modal-background',
                          p: '2'
                        }}
                      >
                        <Flex
                          css={{ margin: '0 auto', width: 105, height: 105 }}
                        >
                          <Flex
                            css={{
                              position: 'absolute',
                              width: 105,
                              height: 105,
                              alignItems: 'center',
                              justifyContent: 'center'
                            }}
                          >
                            <Flex
                              css={{
                                position: 'relative',
                                zIndex: '999',
                                transform: 'translate3d(0,0,0)'
                              }}
                            >
                              <ChainIcon
                                chainId={fromChain?.id}
                                height={14}
                                width={14}
                                css={{
                                  borderRadius: 4,
                                  border: '1.5px solid white',
                                  position: 'absolute',
                                  bottom: 0,
                                  right: 0
                                }}
                              />
                              <img
                                alt={fromToken?.name}
                                src={fromToken?.logoURI}
                                width={33}
                                height={33}
                                style={{
                                  borderRadius: '100%',
                                  border: '1.5px solid white',
                                  background: 'white'
                                }}
                              />
                            </Flex>
                          </Flex>
                          <QRCodeCanvas
                            value={qrcodeUrl}
                            width={105}
                            height={105}
                            level={'H'}
                            imageSettings={{
                              src: 'data:image/png;base64,',
                              height: 33,
                              width: 33,
                              excavate: true
                            }}
                            style={{
                              margin: '0 auto',
                              width: 105,
                              height: 105
                            }}
                          />
                        </Flex>
                      </Box>
                    </Box>
                  </PopoverContent>
                </PopoverPortal>
              </Popover>
            ) : null}
            <CopyToClipBoard text={depositAddress ?? ''} />
          </>
        )}
      </Pill>
      <Button
        disabled={true}
        css={{
          color: 'button-disabled-color !important',
          mt: 8,
          justifyContent: 'center'
        }}
      >
        <LoadingSpinner
          css={{ height: 16, width: 16, fill: 'button-disabled-color' }}
        />
        Waiting for you to transfer funds
      </Button>
    </>
  )
}
